import { takeLatest, put, all, call } from 'redux-saga/effects';

import UserActionTypes from './user.types';

import { auth, googleProvider, createUserProfileDocument } from '../../firebase/firebase.utils';
import { googleSignInSuccess, googleSignInFailure, emailSignInStart, emailSignInSuccess, emailSignInFailure } from './user.actions';

function* signInWithGoogle() {
  // console.log("signing in with google in sagas")
  try{
    const { user } = yield auth.signInWithPopup(googleProvider);
    const userRef = yield call(createUserProfileDocument, user);
    const userSnapshot = yield userRef.get();

    yield put(
      googleSignInSuccess({ id: userSnapshot.id, ...userSnapshot.data() })
    );
  }
  catch ( error ) {
    yield put(googleSignInFailure( error ));
  }
}

function* signInWithEmail({ payload: { email, password }}) {
   try {
      const { user } = yield auth.signInWithEmailAndPassword( email, password );
      const userRef = yield call(createUserProfileDocument, user);
      const userSnapshot = yield userRef.get();

      yield put(
        emailSignInSuccess({ id: userSnapshot.id, ...userSnapshot.data() })
      );

      // await auth.signInWithEmailAndPassword(email, password);
      // setCredentials({email: '', password: ''});
    } 
    catch ( error ) {
      yield put( emailSignInFailure ( error ));
    }
}

//listen for GOOGLE_SIGNIN_START and trigger actual action in reducer
export function* onGoogleSignInStart() {
  yield takeLatest(UserActionTypes.GOOGLE_SIGNIN_START, signInWithGoogle)
}

export function* onEmailSignInStart() {
  yield takeLatest(UserActionTypes.EMAIL_SIGNIN_START, signInWithEmail );
}

export function* userSagas() {
  yield all([
    call(onGoogleSignInStart),
    call(onEmailSignInStart),
  ])
}
