import styled, { css } from 'styled-components';

//component conditionally render css based on props




/**
 * Three different cases of our styled button.
 * Regular, inverted, or google. Define the css for each case.
 */
const regularStyles = css`
    border: none;
    background-color: black;
    color: white;

    &:hover {
      background-color: white;
      color: black;
      border: 1px solid black;
    }
`;

const invertedStyles = css`
    background-color: white;
    color: black;
    border: 1px solid black;

    &:hover {
        background-color: black;
        color: white;
        border:  none;
    }
`;

const googleBlue = '#4285f4';
const googleSigninStyles = css`
    background-color: #4285f4;
    color: white;

    &:hover {
        background-color: darken($color: ${googleBlue}, $amount: 10);
        border: none;
    }
`;

const getButtonStyles = props => {
    if(props.isGoogleSignin) {
        return googleSigninStyles;
    }

    return props.inverted ? invertedStyles : regularStyles;
}

export const CustomButtonContainer = styled.button`
    min-width: 165px;
    width: auto;
    height: 50px;
    letter-spacing: 0.5px;
    line-height: 50px;
    padding: 0 35px 0 35px;
    font-size: 15px;
    text-transform: uppercase;
    font-family: 'Open Sans Condensed';
    font-weight: bolder;
    cursor: pointer;
    display: flex;
    justify-content: center;

    ${getButtonStyles}
`;
