import styled, { css } from 'styled-components';
import { Link } from 'react-router-dom';


export const HeaderContainer = styled.div`
    height: 70px;
    width: 100%;
    display: flex;
    justify-content: space-between;
    margin-bottom: 25px;
`;

/*
 * can call styled on a react component as well as on a DOM element
 * here we call it on a Link because that's what our logo container is
 */
export const LogoContainer = styled(Link)`
    height: 100%;
    width: 70px;
    padding: 25px;
`;

export const OptionsContainer = styled.div`
    width: 50%;
    height: 100%;
    display: flex;
    align-items: center;
    justify-content: flex-end;
`;

//if option is a link
export const OptionLink = styled(Link)`
    padding: 10px 15px;
    cursor: pointer;
`;
