import React from 'react';
import { connect } from 'react-redux';

import { toggleCartHidden } from '../../redux/cart/cart.actions';
import { selectCartItemsCount } from '../../redux/cart/cart.selectors'

import { ReactComponent as ShoppingIcon } from '../../assets/shopping-bag.svg';

import './cart-icon.styles.scss';

const CartIcon = ({bananarama}) => (
    <div className = 'cart-icon' onClick={bananarama}>
        <ShoppingIcon className='shopping-icon' />
        <span className='item-count'>{selectCartItemsCount()}</span>
    </div>
)

const mapDispatchToProps = (dispatch) => ({
    bananarama: () => dispatch(toggleCartHidden())
})

// const mapStateToProps = ({ cart: { cartItems }}) => ({
//     totalQuantity: cartItems.reduce( 
//         (prevQuantity, cartItem) => prevQuantity + cartItem.quantity,
//         0 )
// })

export default connect(null, mapDispatchToProps)(CartIcon);
