import React from 'react';

import './checkout-item.styles.scss';

/*
 * component which displays quantity of a single item on checkout page
 * also has buttons which can dispatch calls to increase or decrease quanity
 */

 const CheckoutItem = ({imageUrl, name, price, quantity}) => (
     <div className='checkout-item'>
        <div className='img-container checkout-cell'>
            <img src={imageUrl}/>
        </div> 
        <span className='checkout-cell'>{name}</span>
        <span className='checkout-cell'>{price}</span>
        <span className='checkout-cell'>{quantity}</span>
     </div>
 )

 export default CheckoutItem;
