import { all, call } from 'redux-saga/effects';

import { fetchCollectionStartSaga } from './shop/shop.sagas';

//put all the sagas we want to run in here
export default function* rootSaga() {
  yield all([
    call(fetchCollectionStartSaga)
  ])
}
